mapper.sql:
FROM shareCodeGroup a
WHERE 1=1
AND DelNy = #{shDelNy}

	AND ccgSeq = #{shValue}
	AND name LIKE CONCAT('%',#{shValue},'%')
	AND name_eng LIKE CONCAT('%',#{shValue},'%')

mapper.select:
SELECT a.* ,(select count(b.cdSeq) from shareCode b where 1=1 and a.DelNy = 0 and a.ccgSeq = b.cdSeq) as ccgSeqcount
ORDER BY a.ccgSeq DESC
)a

mapper.insert:
INSERT INTO codeGroup (
	name
	,name_eng
	,codeGroup_another
	,codeGroup_or
	,DelNy
	,ccgId
)
VALUES (
	#{name}
	,#{name_eng}
	,#{codeGroup_another}
	,#{codeGroup_or}
	,#{ifcgDelNy}
	,#{ccgId}
)


	SELECT last_insert_id()

mapper.select:

select count(*) 
from codeGroup 
where ccgId = #{ccgId}

mapper.update:
	 	UPDATE codeGroup
	 	SET
 			name = #{name}
 			,ccgId = #{ccgId}

		WHERE
			ccgSeq = #{ccgSeq}
