mapper.insert:
INSERT INTO codeGroup (
	name
	,name_eng
	,DelNy
)
VALUES (
	#{name}
	,#{name_eng}
	,#{ifcgDelNy}
)


	SELECT last_insert_id()

mapper.update:
	 	UPDATE codeGroup
	 	SET
 			name = #{name}
	 		,name_eng = #{name_eng}
	 		,DelNy = #{ifcgDelNy}
	 		,codeGroup_another = #{codeGroup_another}
	 		,codeGroup_use = #{codeGroup_use} 
	 		,codeGroup_or = #{codeGroup_or} 
	 		,codeGroup_con = #{codeGroup_con} 
	 		,codeGroup_V1 = #{codeGroup_V1} 
	 		,codeGroup_V2 = #{codeGroup_V2} 
	 		,codeGroup_V3 = #{codeGroup_V3} 
	 		,codeGroup_I1 = #{codeGroup_I1} 
	 		,codeGroup_I2 = #{codeGroup_I2} 
	 		,codeGroup_I3 = #{codeGroup_I3} 
		WHERE
			ccgSeq = #{ccgSeq}
