mapper.sql:
FROM share_member a
WHERE 1=1
AND DelNy = #{shDelNy}

	AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}


	AND a.name LIKE CONCAT('%',#{shValue},'%')
	AND a.nick_nm LIKE CONCAT('%',#{shValue},'%')
	AND a.email LIKE CONCAT('%',#{shValue},'%')

mapper.select:
SELECT a.*
ORDER BY a.seq DESC
)a

mapper.insert:
INSERT INTO share_member (
	id
	,pw
	,name
	,nick_nm
	,share_place
	,dob
	,gender
	,email
	,email_div
	,phone
	,phone_div
	,DelNy
	,createDate
)
VALUES (
	#{id}
	,#{pw}
	,#{name}
	,#{nick_nm}
	,#{share_place}
	,#{dob}
	,#{gender}
	,#{email}
	,#{email_div}
	,#{phone}
	,#{phone_div}
	,0
	,now()
)

	SELECT last_insert_id()

INSERT INTO share_member (
	nick_nm
	,share_place
	,modDate
)
VALUES (
	#{nick_nm}
	,#{share_place}
	,#{modDate}
)

	SELECT last_insert_id()

mapper.select:
select count(*) 
from share_member 
where id = #{id}

	SELECT
		a.id
	FROM share_member a
 	WHERE 1=1
 		AND a.name = #{name}
 		and a.dob = #{dob}
		and a.phone = #{phone}


	SELECT
		count(*) 
	FROM share_member a
 	WHERE 1=1
 		AND name = #{name}
 		and id = #{id}
		and phone = #{phone}

mapper.update:
	UPDATE share_member
	SET
		pw = #{pw}

	where 1=1
		AND name = #{name}
 		and id = #{id}
		and phone = #{phone}

	 	UPDATE share_member
	 	SET
 			name = #{name}
 			,id = #{id}

		WHERE
			seq = #{seq}

	 	UPDATE share_member
	 	SET
 			nick_nm = #{nick_nm}
 			,share_place = #{share_place}

		WHERE
			seq = #{seq}

