mapper.sql:
FROM share_member a
WHERE 1=1
AND DelNy = #{shDelNy}

	AND seq = #{shValue}
	AND name LIKE CONCAT('%',#{shValue},'%')
	AND name_eng LIKE CONCAT('%',#{shValue},'%')

mapper.select:
SELECT a.*
ORDER BY a.seq DESC
)a

