mapper.select:
SELECT c.seq ,c.food_div ,c.title ,c.people_num ,c.info ,c.place ,c.time ,c.price ,c.DelNy ,c.createDate FROM share c WHERE 1=1 and share_member_seq = #{seq}
AND food_div = #{shValue} AND title LIKE CONCAT('%',#{shValue},'%') AND people_num = #{shValue} AND place LIKE CONCAT('%',#{shValue},'%') AND time LIKE CONCAT('%',#{shValue},'%') AND price LIKE CONCAT('%',#{shValue},'%')
ORDER BY c.seq DESC

mapper.insert:

INSERT INTO favorite (
		memberSeq
		,shareSeq
		,likeNy
	)
	VALUES (
		#{memberSeq}
		,#{seq}
		,1
	)


		SELECT last_insert_id()

mapper.update:
UPDATE favorite
SET
	likeNy = #{likeNy}
where 1=1
	and memberSeq = #{memberSeq}
	and shareSeq = #{seq}

