		,#{place}
		,#{time}
		,#{price}
		,#{info}
		,now()
		,#{share_member_seq}
	)


		SELECT last_insert_id()

mapper.update:
 	UPDATE share
 	SET
		food_div = #{food_div}
		,title = #{title}
