mapper.select:
SELECT c.*
ORDER BY c.seq ASC
)a

mapper.insert:
INSERT INTO share (
		food_div
		,title
		,people_num
		,place
		,time
		,price
		,info
		,share_member_seq
	)
	VALUES (
		#{food_div}
		,#{title}
		,#{people_num}
		,#{place}
		,#{time}
		,#{price}
		,#{info}
		,#{share_member_seq}
	)


		SELECT last_insert_id()

mapper.select:

select count(*) 
from share 
where id = #{id}
